fn min_insertions_to_palindrome(s: &str) -> usize {
    // Returns the minimum number of insertions required to make the string a palindrome.
    // Uses dynamic programming approach with a 2D table to store intermediate results.

    let n = s.len();
    if n == 0 {
        return 0;
    }

    // Create a 2D vector for dynamic programming table
    let mut dp = vec![vec![0; n]; n];

    // Fill the table for all substring lengths from 2 to n
    for length in 2..=n {
        for i in 0..=(n - length) {
            let j = i + length - 1;
            if s.chars().nth(i) == s.chars().nth(j) {
                dp[i][j] = dp[i + 1][j - 1];
            } else {
                dp[i][j] = std::cmp::min(dp[i + 1][j], dp[i][j - 1]) + 1;
            }
        }
    }

    dp[0][n - 1]
}

fn process_cases(test_cases: &[&str]) -> Vec<usize> {
    // Processes multiple test cases and returns a vector of results.
    // Each result is the minimum insertions needed to make the corresponding string a palindrome.

    test_cases.iter().map(|s| min_insertions_to_palindrome(s)).collect()
}

