#[test]
fn test_palindrome_insertions() {
    let test_strings = [
        ("ab", 1),      // Needs 1 insertion (aba or bab)
        ("aa", 0),      // Already palindrome (0)
        ("abcd", 3),    // Needs 3 insertions
        ("abcda", 2),   // Needs 2 insertions (adcda or acdca)
        ("abcde", 4),   // Needs 4 insertions
        ("race", 3),    // Needs 3 insertions (ecarace)
        ("abcb", 1),    // Needs 1 insertion (abcba)
        ("", 0),        // Empty string (0)
        ("a", 0),       // Single character (0)
    ];

    for &(s, expected) in &test_strings {
        assert_eq!(min_insertions_to_palindrome(s), expected);
    }

    let batch_results = process_cases(&test_strings.iter().map(|&(s, _)| s).collect::<Vec<_>>());
    for (i, &(_, expected)) in test_strings.iter().enumerate() {
        assert_eq!(batch_results[i], expected);
    }
}

