    // Perfect fit: 5 * 20 = 100
    assert_eq!(can_fit_logos(100, 5, 20), true);
    // Perfect fit: 3 * 50 = 150
    assert_eq!(can_fit_logos(150, 3, 50), true);
    // Not a perfect fit: 7 * 30 = 210 != 200
    assert_eq!(can_fit_logos(200, 7, 30), false);
    // Edge case: 0 logos fit in a 0-length banner
    assert_eq!(can_fit_logos(0, 0, 10), true);
    // Single logo fits perfectly
    assert_eq!(can_fit_logos(10, 1, 10), true);
}

