fn pixel_forth(commands: &str) -> Vec<Vec<u8>> {
    // Initialize a 10x10 grid with all zeros
    let mut grid = vec![vec![0; 10]; 10];
    let mut cursor_x: usize = 0;
    let mut cursor_y: usize = 0;
    let mut i = 0;
    let commands: Vec<char> = commands.chars().collect();

    while i < commands.len() {
        let command = commands[i];
        match command {
            '>' => cursor_y = (cursor_y + 1) % 10,
            '<' => cursor_y = (cursor_y as isize - 1).rem_euclid(10) as usize,
            '^' => cursor_x = (cursor_x as isize - 1).rem_euclid(10) as usize,
            'v' => cursor_x = (cursor_x + 1) % 10,
            'X' => grid[cursor_x][cursor_y] = 1 - grid[cursor_x][cursor_y],
            'O' => grid[cursor_x][cursor_y] = 1,
            'F' => grid[cursor_x][cursor_y] = 0,
            '[' => {
                if grid[cursor_x][cursor_y] == 0 {
                    let mut open_brackets = 1;
                    while open_brackets > 0 {
                        i += 1;
                        match commands[i] {
                            '[' => open_brackets += 1,
                            ']' => open_brackets -= 1,
                            _ => (),
                        }
                    }
                }
            }
            ']' => {
                if grid[cursor_x][cursor_y] == 1 {
                    let mut closed_brackets = 1;
                    while closed_brackets > 0 {
                        i -= 1;
                        match commands[i] {
                            ']' => closed_brackets += 1,
                            '[' => closed_brackets -= 1,
                            _ => (),
                        }
                    }
                }
            }
            '!' => {
                grid = grid
                    .iter()
                    .map(|row| row.iter().map(|&cell| 1 - cell).collect())
                    .collect();
            }
            'P' => {
                // Print the grid
                for row in &grid {
                    println!(
                        "{}",
                        row.iter()
                            .map(|&cell| cell.to_string())
                            .collect::<Vec<String>>()
                            .join(" ")
                    );
                }
            }
            _ => (),
        }
        i += 1;
    }

    grid
}

