fn max_knapsack(weight_limit: usize, weights: &[usize], values: &[usize]) -> usize {
    // Function to find the maximum value of items fitting within the given weight limit
    // using dynamic programming approach.
    //
    // Args:
    // weight_limit: Maximum weight capacity of the knapsack
    // weights: Slice of weights of the items
    // values: Slice of values of the items
    //
    // Returns:
    // Maximum value that can be achieved within the given weight limit

    // Initialize a DP array where dp[w] represents the maximum value achievable with weight 'w'
    let mut dp = vec![0; weight_limit + 1];

    // Iterate through each item
    for i in 0..values.len() {
        // Iterate from the weight limit down to the current item's weight
        for w in (weights[i]..=weight_limit).rev() {
            // Update the DP array by either keeping the current value or adding the current item
            dp[w] = dp[w].max(dp[w - weights[i]] + values[i]);
        }
    }

    // The last element of DP array contains the maximum value for the given weight limit
    dp[weight_limit]
}

assertions:
#[test]
fn test_max_knapsack() {
    // Test case 1: Simple case with 3 items
    assert_eq!(max_knapsack(5, &[1, 2, 3], &[6, 10, 12]), 22);

    // Test case 2: All items can be taken
    assert_eq!(max_knapsack(10, &[2, 3, 4], &[5, 6, 7]), 18);

    // Test case 3: Only one item can be taken
    assert_eq!(max_knapsack(3, &[4, 5, 6], &[1, 2, 3]), 0);

    // Test case 4: Edge case with zero weight limit
    assert_eq!(max_knapsack(0, &[1, 2, 3], &[4, 5, 6]), 0);

    // Test case 5: Multiple items with same weight but different values
    assert_eq!(max_knapsack(7, &[2, 2, 3, 3], &[5, 6, 7, 8]), 21);
}

