use std::cmp::Ordering;

// Represents a graph edge with source, destination, and weight
#[derive(Debug, Clone, Copy)]
struct Edge {
    source: usize,
    destination: usize,
    weight: i32,
}

impl Edge {
    fn new(source: usize, destination: usize, weight: i32) -> Self {
        Edge {
            source,
            destination,
            weight,
        }
    }
}

// Implement ordering for Edge based on weight for sorting
impl PartialOrd for Edge {
    fn partial_cmp(&self, other: &Self) -> Option<Ordering> {
        Some(self.weight.cmp(&other.weight))
    }
}

impl Ord for Edge {
    fn cmp(&self, other: &Self) -> Ordering {
        self.weight.cmp(&other.weight)
    }
}

impl PartialEq for Edge {
    fn eq(&self, other: &Self) -> bool {
        self.weight == other.weight
    }
}

impl Eq for Edge {}

// Union-Find (Disjoint Set Union) data structure for Kruskal's algorithm
struct UnionFind {
    parent: Vec<usize>,
    rank: Vec<usize>,
}

impl UnionFind {
    fn new(size: usize) -> Self {
        UnionFind {
            parent: (0..size).collect(),
            rank: vec![0; size],
        }
    }

    // Find with path compression
    fn find(&mut self, x: usize) -> usize {
        if self.parent[x] != x {
            self.parent[x] = self.find(self.parent[x]);
        }
        self.parent[x]
    }

    // Union by rank
    fn union(&mut self, x: usize, y: usize) {
        let root_x = self.find(x);
        let root_y = self.find(y);

        if root_x != root_y {
            match self.rank[root_x].cmp(&self.rank[root_y]) {
                Ordering::Less => self.parent[root_x] = root_y,
                Ordering::Greater => self.parent[root_y] = root_x,
                Ordering::Equal => {
                    self.parent[root_y] = root_x;
                    self.rank[root_x] += 1;
                }
            }
        }
    }
}

// Computes the weight sum of the edges in the minimum spanning tree
fn minimum_spanning_tree(vertices: usize, edges: &mut Vec<Edge>) -> i32 {
    // Sort edges by weight in ascending order
    edges.sort();

    let mut uf = UnionFind::new(vertices + 1); // +1 because vertices are 1-based in the problem
    let mut mst_weight = 0;
    let mut edges_in_mst = 0;

    for &edge in edges.iter() {
        if uf.find(edge.source) != uf.find(edge.destination) {
            uf.union(edge.source, edge.destination);
            mst_weight += edge.weight;
            edges_in_mst += 1;

            // A tree with n vertices has n-1 edges
            if edges_in_mst == vertices - 1 {
                break;
            }
        }
    }

    mst_weight
}

assertions:
#[test]
fn test_minimum_spanning_tree() {
    // Test case 1: Simple graph with 3 vertices and 3 edges
    let mut edges1 = vec![
        Edge::new(1, 2, 1),
        Edge::new(2, 3, 2),
        Edge::new(1, 3, 3),
    ];
    assert_eq!(minimum_spanning_tree(3, &mut edges1), 3);

    // Test case 2: Graph with 4 vertices and 5 edges
    let mut edges2 = vec![
        Edge::new(1, 2, 4),
        Edge::new(1, 3, 1),
        Edge::new(2, 3, 2),
        Edge::new(2, 4, 3),
        Edge::new(3, 4, 5),
    ];
    assert_eq!(minimum_spanning_tree(4, &mut edges2), 6);

    // Test case 3: Graph with single edge
    let mut edges3 = vec![Edge::new(1, 2, 10)];
    assert_eq!(minimum_spanning_tree(2, &mut edges3), 10);

    // Test case 4: Disconnected graph (should return MST for connected component)
    let mut edges4 = vec![
        Edge::new(1, 2, 5),
        Edge::new(3, 4, 7),
    ];
    assert_eq!(minimum_spanning_tree(4, &mut edges4), 12);
