/// Determines whether all given rectangular pieces can fit on a board of specified dimensions
/// based solely on total area comparison (does not account for actual piece arrangement).
///
/// # Arguments
/// * `board_width` - Width of the board
/// * `board_height` - Height of the board
/// * `pieces` - Vector of tuples representing piece dimensions (width, height)
///
/// # Returns
/// "YES" if the total area of pieces is less than or equal to the board area, "NO" otherwise
fn can_place_all_pieces(board_width: u32, board_height: u32, pieces: &[(u32, u32)]) -> &'static str {
    // Calculate total area of all pieces
    let total_piece_area: u32 = pieces.iter().map(|(w, h)| w * h).sum();

    // Calculate board area
    let board_area = board_width * board_height;

    // Compare areas
    if total_piece_area <= board_area {
        "YES"
    } else {
        "NO"
    }
}

