    assert_eq!(can_place_all_pieces(8, 4, &[(4, 4), (4, 4)]), "YES");
    assert_eq!(can_place_all_pieces(3, 3, &[(1, 1); 10]), "NO");
    assert_eq!(can_place_all_pieces(100, 1, &[(1, 100), (1, 1)]), "NO");
}

