fn calculate_subarray_averages(data: &[i32], k: usize) -> Vec<f64> {
    // Calculates the average of every contiguous subarray of length k in the given data slice.
    //
    // Parameters:
    // data: Slice of integers
    // k: Length of subarray
    //
    // Returns:
    // A vector of averages of each contiguous subarray of length k
    let n = data.len();
    if n == 0 || k > n {
        return Vec::new();
    }

    let mut subarray_averages = Vec::with_capacity(n - k + 1);
    let mut current_sum: i32 = data[..k].iter().sum();
    subarray_averages.push(current_sum as f64 / k as f64);

    for i in k..n {
        current_sum += data[i] - data[i - k];
        subarray_averages.push(current_sum as f64 / k as f64);
    }

    subarray_averages
}

assertions:
