    assert_eq!(
        calculate_subarray_averages(&[1, 3, 2, 6, -1, 4, 1, 8, 2], 5),
        vec![2.2, 2.8, 2.4, 3.6, 2.8]
    );
    assert_eq!(
        calculate_subarray_averages(&[1, 2, 3, 4, 5], 2),
        vec![1.5, 2.5, 3.5, 4.5]
    );
    assert_eq!(
        calculate_subarray_averages(&[10, 20, 30, 40], 4),
        vec![25.0]
    );
    assert_eq!(
        calculate_subarray_averages(&[5], 1),
        vec![5.0]
    );
    assert_eq!(
        calculate_subarray_averages(&[], 3),
        vec![]
    );
    assert_eq!(
        calculate_subarray_averages(&[1, 2, 3], 5),
        vec![]
    );
}

