// Disjoint Set Union (DSU) implementation for Kruskal's algorithm
struct DSU {
    parent: Vec<usize>,
    rank: Vec<usize>,
}

impl DSU {
    // Create a new DSU with n elements (0..n-1)
    fn new(n: usize) -> Self {
        DSU {
            parent: (0..n).collect(),
            rank: vec![0; n],
        }
    }

    // Find the root of the set containing element i
    fn find(&mut self, i: usize) -> usize {
        if self.parent[i] != i {
            self.parent[i] = self.find(self.parent[i]);
        }
        self.parent[i]
    }

    // Union the sets containing elements x and y
    fn union(&mut self, x: usize, y: usize) {
        let root_x = self.find(x);
        let root_y = self.find(y);

        if root_x != root_y {
            if self.rank[root_x] > self.rank[root_y] {
                self.parent[root_y] = root_x;
            } else if self.rank[root_x] < self.rank[root_y] {
                self.parent[root_x] = root_y;
            } else {
                self.parent[root_y] = root_x;
                self.rank[root_x] += 1;
            }
        }
    }
}

// Calculate the minimum spanning tree cost using Kruskal's algorithm
fn minimum_spanning_tree(n: usize, edges: &mut Vec<(usize, usize, i32)>) -> i32 {
