// Function to check if a matrix is symmetric
fn is_symmetric(matrix: &[Vec<i32>]) -> &'static str {
    // Get the size of the matrix (n x n)
    let n = matrix.len();

    // Check each element against its transposed counterpart
    for i in 0..n {
        for j in i..n {
            if matrix[i][j] != matrix[j][i] {
                return "NO";
            }
        }
    }
    "YES"
}

