use std::collections::HashSet;

/// Calculates the number of valid Scrabble words that can be formed using exactly `k` characters
/// from a given set of `n` distinct characters. This is computed as the number of permutations
/// of `k` characters from the set of `n` characters, which is `n! / (n - k)!`.
///
/// # Arguments
/// * `n` - The number of distinct characters.
/// * `characters` - A string slice containing the distinct characters.
/// * `k` - The length of the words to form.
///
/// # Returns
/// * `i32` - The number of valid Scrabble words, or 0 if `k` exceeds `n`.
fn count_scrabble_words(n: usize, characters: &str, k: usize) -> i32 {
    // First, validate that all characters are distinct and the length matches `n`
    let unique_chars: HashSet<char> = characters.chars().collect();
    if unique_chars.len() != n {
        return 0;
    }

    if k > n {
        return 0;
    }

    // Calculate the permutation: n! / (n - k)!
    let numerator: i32 = (1..=n).product::<usize>() as i32;
    let denominator: i32 = (1..=(n - k)).product::<usize>() as i32;
    numerator / denominator
}

assertions:
#[test]
fn test_count_scrabble_words() {
    // Valid cases
    assert_eq!(count_scrabble_words(3, "abc", 2), 6);
    assert_eq!(count_scrabble_words(4, "abcd", 3), 24);
    assert_eq!(count_scrabble_words(5, "abcde", 5), 120);

    // Invalid cases
    assert_eq!(count_scrabble_words(2, "aab", 1), 0);  // Characters not distinct
    assert_eq!(count_scrabble_words(3, "xyz", 4), 0);  // k > n
}

