    assert_eq!(min_operations_to_limit_max(4, 5, &[2, 3, 4, 1]), 0);
    assert_eq!(min_operations_to_limit_max(3, 0, &[0, 0, 0]), 0);
    assert_eq!(min_operations_to_limit_max(6, 10, &[15, 12, 8, 20, 9, 11]), 10);
    assert_eq!(min_operations_to_limit_max(0, 5, &[]), 0);
}

