use std::collections::{VecDeque, HashSet};

/// Finds the minimum energy required to reach the destination 'D' from the start 'S' in a grid.
/// The grid may contain obstacles marked by '#' and empty cells marked by '.'.
/// Uses BFS to find the shortest path (minimum energy) from 'S' to 'D'.
/// Returns -1 if the destination is unreachable.
fn min_energy_to_reach_destination(n: usize, m: usize, grid: &Vec<Vec<char>>) -> i32 {
    // Find the start and destination positions
    let mut start = (0, 0);
    let mut end = (0, 0);
    let mut found_start = false;
    let mut found_end = false;

    for i in 0..n {
        for j in 0..m {
            if grid[i][j] == 'S' {
                start = (i, j);
                found_start = true;
            } else if grid[i][j] == 'D' {
                end = (i, j);
                found_end = true;
            }
        }
    }

    if !found_start || !found_end {
        return -1;
    }

    // BFS queue: stores (row, column, current_energy)
    let mut queue = VecDeque::new();
    queue.push_back((start.0, start.1, 0));

    // Visited set to keep track of visited positions
    let mut visited = HashSet::new();
    visited.insert((start.0, start.1));

    // Possible movement directions: up, down, left, right
    let directions = [(-1, 0), (1, 0), (0, -1), (0, 1)];

    while let Some((x, y, energy)) = queue.pop_front() {
        // Check if current position is the destination
        if (x, y) == end {
            return energy;
        }

        // Explore all four possible directions
        for &(dx, dy) in &directions {
            let nx = (x as i32 + dx) as usize;
            let ny = (y as i32 + dy) as usize;

            // Check if new position is within bounds, not visited, and not an obstacle
            if nx < n && ny < m && !visited.contains(&(nx, ny)) && grid[nx][ny] != '#' {
                visited.insert((nx, ny));
                queue.push_back((nx, ny, energy + 1));
            }
        }
    }

    -1
}

