    ];
    assert_eq!(min_energy_to_reach_destination(3, 3, &grid1), 4);

    // Test case 2: Grid with no possible path
    let grid2 = vec![
        vec!['S', '#', '#'],
        vec!['#', '#', '#'],
        vec!['#', '#', 'D'],
    ];
