    // Test case 3: Straight path with no obstacles
    let grid3 = vec![
        vec!['S', '.', '.', 'D'],
    ];
    assert_eq!(min_energy_to_reach_destination(1, 4, &grid3), 3);

    // Test case 4: Grid with obstacle block in middle
    let grid4 = vec![
        vec!['S', '.', '.', '.'],
        vec!['.', '#', '#', '.'],
        vec!['.', '#', '#', '.'],
        vec!['.', '.', '.', 'D'],
