fn longest_ones(nums: &[i32]) -> i32 {
    // Finds the maximum number of consecutive 1's with at most one 0 in between.
    // Uses a sliding window approach with left and right pointers.

    let mut left = 0;
    let mut max_ones = 0;
    let mut zero_count = 0;

    for right in 0..nums.len() {
        if nums[right] == 0 {
            zero_count += 1;
        }

        // When we have more than one zero, move left pointer forward
        // until we have at most one zero in the window
        while zero_count > 1 {
            if nums[left] == 0 {
                zero_count -= 1;
            }
            left += 1;
        }

        // Update the maximum window size
        max_ones = max_ones.max((right - left + 1) as i32);
    }

    max_ones
}

