    assert_eq!(longest_ones(&[1, 1, 0, 1, 1, 1]), 5);
    assert_eq!(longest_ones(&[1, 0, 1, 1, 0, 1]), 4);
    assert_eq!(longest_ones(&[0, 1, 1, 1, 0, 1, 1, 0, 1]), 5);
    assert_eq!(longest_ones(&[1, 1, 1, 1, 1]), 5);
    assert_eq!(longest_ones(&[0, 0, 0, 0]), 1);
    assert_eq!(longest_ones(&[1, 0, 0, 1, 0, 1, 0, 1]), 2);
}

