use std::collections::HashMap;

/// Finds the most frequent substring of length k in the input string.
/// If there are multiple substrings with the same maximum frequency,
/// returns the lexicographically smallest one.
/// Returns an empty string if k is greater than the input string length.
fn most_frequent_substring(input_string: &str, k: usize) -> String {
    if k > input_string.len() {
        return String::new();
    }

    let mut substring_counts = HashMap::new();

    // Count occurrences of each substring of length k
    for i in 0..=(input_string.len() - k) {
        let substring = &input_string[i..i + k];
        *substring_counts.entry(substring.to_string()).or_insert(0) += 1;
    }

    // Find the maximum frequency
    let max_frequency = substring_counts.values().max().copied().unwrap_or(0);

    // Collect all substrings with maximum frequency
    let mut most_frequent_substrings: Vec<String> = substring_counts
        .iter()
        .filter(|&(_, &count)| count == max_frequency)
        .map(|(s, _)| s.clone())
        .collect();

    // Return the lexicographically smallest substring
    most_frequent_substrings.sort();
    most_frequent_substrings.into_iter().next().unwrap_or_default()
}
