    assert_eq!(most_frequent_substring("aabbb", 2), "bb");
    assert_eq!(most_frequent_substring("short", 10), "");
    assert_eq!(most_frequent_substring("banana", 2), "an");
    assert_eq!(most_frequent_substring("", 2), "");
}

