use std::collections::HashMap;

/// Determines if a given sum can be formed using any combination of given coin denominations.
///
/// # Arguments
/// * `n` - Number of different coin denominations.
/// * `s` - The sum to form.
/// * `coins` - List of coin denominations.
///
/// # Returns
/// "POSSIBLE" if the sum can be formed using the given coin denominations, "IMPOSSIBLE" otherwise.
fn is_sum_possible(n: usize, s: usize, coins: &[usize]) -> String {
    // Initialize a vector to store dp values
    let mut dp = vec![false; s + 1];
    dp[0] = true; // Base case: We can always form the sum 0

    for &coin in coins {
        for x in coin..=s {
            if dp[x - coin] {
                dp[x] = true;
            }
        }
    }

    if dp[s] {
        "POSSIBLE".to_string()
    } else {
        "IMPOSSIBLE".to_string()
    }
}

/// Checks the possibility of forming sums for multiple queries.
///
/// # Arguments
/// * `queries` - A vector of tuples where each tuple contains (n, s, coins).
///
/// # Returns
/// A vector of results indicating "POSSIBLE" or "IMPOSSIBLE" for each query.
fn check_possibility(queries: Vec<(usize, usize, Vec<usize>)>) -> Vec<String> {
    queries
        .into_iter()
        .map(|(n, s, coins)| is_sum_possible(n, s, &coins))
        .collect()
}

