/// Determines whether a sequence of numbers is arithmetic, geometric, or neither.
/// An arithmetic sequence has a constant difference between consecutive terms.
/// A geometric sequence has a constant ratio between consecutive terms.
/// Returns "Arithmetic", "Geometric", or "Neither" as appropriate.
fn sequence_type(nums: &[f64]) -> &'static str {
    if nums.len() < 2 {
        return "Neither";
    }

    let mut is_arithmetic = true;
    let mut is_geometric = true;

    // Check for arithmetic sequence
    let common_difference = nums[1] - nums[0];
    for i in 1..nums.len() {
        if (nums[i] - nums[i - 1] - common_difference).abs() > f64::EPSILON {
            is_arithmetic = false;
            break;
        }
    }

    // Check for geometric sequence
    if nums[0].abs() < f64::EPSILON {
        is_geometric = false;
    } else {
        let common_ratio = nums[1] / nums[0];
        for i in 1..nums.len() {
            if nums[i].abs() < f64::EPSILON || 
               ((nums[i] / nums[i - 1]) - common_ratio).abs() > f64::EPSILON {
                is_geometric = false;
                break;
            }
        }
    }

    if is_arithmetic {
        "Arithmetic"
    } else if is_geometric {
        "Geometric"
    } else {
        "Neither"
    }
}

assertions:
#[test]
fn test_sequence_type() {
    assert_eq!(sequence_type(&[1.0, 2.0, 3.0, 4.0]), "Arithmetic");
    assert_eq!(sequence_type(&[2.0, 4.0, 8.0, 16.0]), "Geometric");
    assert_eq!(sequence_type(&[1.0, 2.0, 4.0, 7.0]), "Neither");
    assert_eq!(sequence_type(&[0.0, 0.0, 0.0, 0.0]), "Arithmetic");
    assert_eq!(sequence_type(&[1.0, 0.0, 0.0, 0.0]), "Neither");
    assert_eq!(sequence_type(&[5.0]), "Neither");
    assert_eq!(sequence_type(&[3.0, -1.0, -5.0, -9.0]), "Arithmetic");
    assert_eq!(sequence_type(&[1.0, -2.0, 4.0, -8.0]), "Geometric");
}

