fn is_wildcard_balanced(s: &str) -> bool {
    // Checks if the given string with '(', ')', and '*' can be balanced.
    // The algorithm tracks the possible minimum (low) and maximum (high) number of open parentheses.
    // If at any point high becomes negative, the string cannot be balanced.
    // Finally, we check if low is zero, meaning we can balance the parentheses.

    let mut low = 0;
    let mut high = 0;

    for char in s.chars() {
        match char {
            '(' => {
                low += 1;
                high += 1;
            }
            ')' => {
                if low > 0 {
                    low -= 1;
                }
                high -= 1;
            }
            '*' => {
                if low > 0 {
                    low -= 1;
                }
                high += 1;
            }
            _ => {} // Ignore other characters
        }

        if high < 0 {
            return false;
        }
    }

    low == 0
}

assertions:
#[test]
fn test_is_wildcard_balanced() {
    assert_eq!(is_wildcard_balanced("()"), true);
    assert_eq!(is_wildcard_balanced("(*)"), true);
    assert_eq!(is_wildcard_balanced("(*))"), true);
    assert_eq!(is_wildcard_balanced("((*)"), true);
    assert_eq!(is_wildcard_balanced("(((***"), true);
    assert_eq!(is_wildcard_balanced(")*("), false);
    assert_eq!(is_wildcard_balanced("((*)*)"), true);
    assert_eq!(is_wildcard_balanced("(*()"), true);
    assert_eq!(is_wildcard_balanced("(*)))"), false);
    assert_eq!(is_wildcard_balanced("((*"), false);
}

