// Function to perform heap sort on a vector of integers
// Parameters:
// - arr: A mutable reference to a vector of i32 to be sorted
// - ascending: A boolean indicating whether to sort in ascending (true) or descending (false) order
// Returns: The sorted vector
fn heap_sort(arr: &mut Vec<i32>, ascending: bool) -> Vec<i32> {
    // Inner function to heapify a subtree rooted at index i
    // n is the size of the heap
    fn heapify(arr: &mut [i32], n: usize, i: usize, ascending: bool) {
        let mut largest = i; // Initialize largest as root
        let left = 2 * i + 1; // left child
        let right = 2 * i + 2; // right child

        // Find the largest/smallest element depending on sort order
        if ascending {
            if left < n && arr[left] > arr[largest] {
                largest = left;
            }
            if right < n && arr[right] > arr[largest] {
                largest = right;
            }
        } else {
            if left < n && arr[left] < arr[largest] {
                largest = left;
            }
            if right < n && arr[right] < arr[largest] {
                largest = right;
            }
        }

        // If largest is not root, swap and continue heapifying
        if largest != i {
            arr.swap(i, largest);
            heapify(arr, n, largest, ascending);
        }
    }

    let n = arr.len();

    // Build heap (rearrange array)
    for i in (0..n / 2).rev() {
        heapify(arr, n, i, ascending);
    }

    // One by one extract elements from heap
    for i in (1..n).rev() {
        arr.swap(0, i); // Move current root to end
        heapify(arr, i, 0, ascending); // Heapify the reduced heap
    }

    arr.to_vec()
}

