    // Test ascending sort
    let mut input = vec![4, 10, 3, 5, 1];
    assert_eq!(heap_sort(&mut input, true), vec![1, 3, 4, 5, 10]);

    // Test descending sort
    let mut input = vec![4, 10, 3, 5, 1];
    assert_eq!(heap_sort(&mut input, false), vec![10, 5, 4, 3, 1]);

    // Test already sorted ascending
    let mut input = vec![1, 2, 3, 4, 5];
    assert_eq!(heap_sort(&mut input, true), vec![1, 2, 3, 4, 5]);

    // Test already sorted descending
    let mut input = vec![5, 4, 3, 2, 1];
    assert_eq!(heap_sort(&mut input, false), vec![5, 4, 3, 2, 1]);

    // Test single element
    let mut input = vec![1];
    assert_eq!(heap_sort(&mut input, true), vec![1]);

    // Test empty vector
    let mut input = vec![];
    assert_eq!(heap_sort(&mut input, true), vec![]);
}

