use std::collections::{HashMap, HashSet};

// Counts the number of connected components in an undirected graph and returns the components
// The graph is represented as an adjacency list (HashMap where keys are nodes and values are neighbors)
fn count_connected_components(graph: &HashMap<u32, Vec<u32>>) -> (usize, Vec<Vec<u32>>) {
    // Performs DFS to explore a connected component starting from 'node'
    // Modifies the 'visited' set and 'component' vector in place
    fn dfs(node: u32, graph: &HashMap<u32, Vec<u32>>, visited: &mut HashSet<u32>, component: &mut Vec<u32>) {
        visited.insert(node);
        component.push(node);

        if let Some(neighbors) = graph.get(&node) {
            for &neighbor in neighbors {
                if !visited.contains(&neighbor) {
                    dfs(neighbor, graph, visited, component);
                }
            }
        }
    }

    let mut visited = HashSet::new();
    let mut components = Vec::new();

    // Iterate through all nodes in the graph
    for &node in graph.keys() {
        if !visited.contains(&node) {
            let mut component = Vec::new();
            dfs(node, graph, &mut visited, &mut component);
            components.push(component);
        }
    }

    (components.len(), components)
}

assertions:
#[test]
fn test_count_connected_components() {
    // Test case 1: Single connected component
    let graph1 = HashMap::from([
        (1, vec![2, 3]),
        (2, vec![1, 3]),
        (3, vec![1, 2]),
    ]);
    let (count1, components1) = count_connected_components(&graph1);
    assert_eq!(count1, 1);
    assert_eq!(components1, vec![vec![1, 2, 3]]);

    // Test case 2: Two connected components
    let graph2 = HashMap::from([
        (1, vec![2]),
        (2, vec![1]),
        (3, vec![4]),
        (4, vec![3]),
    ]);
    let (count2, components2) = count_connected_components(&graph2);
    assert_eq!(count2, 2);
    assert!(components2.contains(&vec![1, 2]));
    assert!(components2.contains(&vec![3, 4]));

    // Test case 3: Three connected components (including isolated nodes)
    let graph3 = HashMap::from([
        (1, vec![2]),
        (2, vec![1]),
        (3, vec![]),
        (4, vec![5]),
        (5, vec![4]),
    ]);
    let (count3, components3) = count_connected_components(&graph3);
    assert_eq!(count3, 3);
    assert!(components3.contains(&vec![1, 2]));
    assert!(components3.contains(&vec![3]));
    assert!(components3.contains(&vec![4, 5]));

    // Test case 4: Empty graph
    let graph4 = HashMap::new();
    let (count4, components4) = count_connected_components(&graph4);
    assert_eq!(count4, 0);
    assert_eq!(components4, Vec::<Vec<u32>>::new());
}

