use std::collections::HashSet;

/// Generates a sequence of length n starting with 1 and adding the difference d.
/// If a number recurs, it continues from the next number until the sequence reaches length n.
///
/// # Arguments
/// * `n` - Length of the sequence
/// * `d` - Difference to be added
///
/// # Returns
/// A vector of n unique numbers starting from 1, or an empty vector if not possible.
fn generate_sequence(n: usize, d: i32) -> Vec<i32> {
    let mut sequence = Vec::with_capacity(n);
    let mut current_num = 1;
    let mut seen_numbers = HashSet::new();

    while sequence.len() < n {
        if !seen_numbers.contains(&current_num) {
            sequence.push(current_num);
            seen_numbers.insert(current_num);
        }
        current_num += d;
    }

    sequence
}

