use std::collections::HashMap;

/// Returns the minimum number of adjacent swaps required to make nums1 equal to nums2.
/// Returns -1 if it's impossible to make them equal through swaps.
fn min_swaps_to_equal(nums1: &[i32], nums2: &[i32]) -> i32 {
    // First check if the sorted versions are equal (i.e., same elements)
    let mut sorted_nums1 = nums1.to_vec();
    let mut sorted_nums2 = nums2.to_vec();
    sorted_nums1.sort();
    sorted_nums2.sort();

    if sorted_nums1 != sorted_nums2 {
        return -1;
    }

    // Create a mapping from value to its index in nums2
    let mut index_map: HashMap<i32, usize> = HashMap::new();
    for (i, &value) in nums2.iter().enumerate() {
        index_map.insert(value, i);
    }

    // We'll work on a mutable copy of nums2
    let mut nums2_mut = nums2.to_vec();
    let mut swaps = 0;

    for i in 0..nums1.len() {
        while nums1[i] != nums2_mut[i] {
            // Get the current position of the element that should be at position i
            let swap_index = *index_map.get(&nums1[i]).unwrap();

            // Perform the swap
            nums2_mut.swap(i, swap_index);

            // Update the index map for the swapped elements
            index_map.insert(nums2_mut[i], i);
            index_map.insert(nums2_mut[swap_index], swap_index);

            swaps += 1;
        }
    }

    swaps
}

