    assert_eq!(min_swaps_to_equal(&[1, 2, 3, 4], &[1, 3, 2, 4]), 1);
    assert_eq!(min_swaps_to_equal(&[1, 2, 3, 4], &[4, 3, 2, 1]), 3);
    assert_eq!(min_swaps_to_equal(&[1, 2, 3, 4], &[1, 2, 4, 3]), 1);
    assert_eq!(min_swaps_to_equal(&[1, 2, 3, 4], &[1, 2, 3, 5]), -1);
    assert_eq!(min_swaps_to_equal(&[5, 1, 2, 4, 3], &[1, 5, 4, 2, 3]), 3);
}

