/// Restores a sequence such that it becomes strictly increasing by replacing zeros.
///
/// # Arguments
/// * `n` - The length of the sequence.
/// * `k` - The maximum allowed value in the sequence.
/// * `sequence` - A mutable vector representing the sequence where zeros need to be replaced.
///
/// # Returns
/// A tuple containing:
/// - "YES" if the sequence can be made strictly increasing, "NO" otherwise
/// - The restored sequence (if successful)
fn restore_sequence(n: usize, k: usize, sequence: &mut Vec<usize>) -> (String, Option<Vec<usize>>) {
    // Create a list of all the missing positions (where value is 0)
    let missing_indices: Vec<usize> = sequence.iter()
        .enumerate()
        .filter(|(_, &x)| x == 0)
        .map(|(i, _)| i)
        .collect();

    // Count unique non-zero values in the sequence
    let unique_non_zero: usize = sequence.iter()
        .filter(|&&x| x != 0)
        .collect::<std::collections::HashSet<_>>()
        .len();

    // Validate if it's possible to make the sequence strictly increasing
    if missing_indices.len() + unique_non_zero > k {
        return ("NO".to_string(), None);
    }

    // Fill in the missing values to make the sequence strictly increasing
    let mut current_value = 1;
    for i in 0..n {
        if sequence[i] == 0 {
            while sequence.contains(&current_value) {
                current_value += 1;
            }
            if current_value > k {
                return ("NO".to_string(), None);
            }
            sequence[i] = current_value;
        }
        current_value = sequence[i] + 1;
    }

    ("YES".to_string(), Some(sequence.clone()))
}

assertions:
#[test]
fn test_restore_sequence() {
    // Test case 1: Can be restored
    let mut seq1 = vec![1, 0, 0, 0, 5];
    let (res1, restored1) = restore_sequence(5, 5, &mut seq1);
    assert_eq!(res1, "YES");
    assert_eq!(restored1, Some(vec![1, 2, 3, 4, 5]));

    // Test case 2: Can't be restored (k too small)
    let mut seq2 = vec![1, 0, 0, 0, 5];
    let (res2, restored2) = restore_sequence(5, 4, &mut seq2);
    assert_eq!(res2, "NO");
    assert_eq!(restored2, None);

    // Test case 3: All zeros
    let mut seq3 = vec![0, 0, 0, 0, 0];
    let (res3, restored3) = restore_sequence(5, 6, &mut seq3);
    assert_eq!(res3, "YES");
    assert_eq!(restored3, Some(vec![1, 2, 3, 4, 5]));

    // Test case 4: Already complete
    let mut seq4 = vec![1, 2, 3];
    let (res4, restored4) = restore_sequence(3, 3, &mut seq4);
    assert_eq!(res4, "YES");
    assert_eq!(restored4, Some(vec![1, 2, 3]));

    // Test case 5: Some zeros
    let mut seq5 = vec![1, 0, 3, 0];
    let (res5, restored5) = restore_sequence(4, 4, &mut seq5);
    assert_eq!(res5, "YES");
    assert_eq!(restored5, Some(vec![1, 2, 3, 4]));
}

