use std::collections::HashMap;

/// Counts how many strings in the input vector have exactly the same character
/// frequency as the word "hello".
///
/// # Arguments
/// * `n` - The number of strings in the input vector (unused in the function but kept for interface consistency)
/// * `strings` - A vector of strings to check against the "hello" character frequency
///
/// # Returns
/// The count of strings that match the "hello" character frequency exactly
fn count_hello_strings(n: usize, strings: Vec<&str>) -> usize {
    let keyword = "hello";

    // Create a frequency map for the keyword "hello"
    let keyword_map = create_char_frequency_map(keyword);

    // Count how many strings have the exact same character frequency
    strings.iter()
        .filter(|&s| create_char_frequency_map(s) == keyword_map)
        .count()
}

/// Creates a frequency map (HashMap) of characters in a string
///
/// # Arguments
/// * `s` - The input string to analyze
///
/// # Returns
/// A HashMap where keys are characters and values are their counts in the string
fn create_char_frequency_map(s: &str) -> HashMap<char, usize> {
    let mut map = HashMap::new();
    for c in s.chars() {
        *map.entry(c).or_insert(0) += 1;
    }
    map
}

