    assert_eq!(count_hello_strings(4, vec!["ehllo", "holle", "llohe", "hlloe"]), 4);
    assert_eq!(count_hello_strings(2, vec!["hello", "nothello"]), 1);
    assert_eq!(count_hello_strings(1, vec!["hell"]), 0);
}

