use std::collections::HashMap;

/// Determines if s1 can be made equal to s2 using the provided swap pairs.
///
/// # Arguments
/// * `s1` - The first string.
/// * `s2` - The second string.
/// * `pairs` - Vector of tuples indicating allowable swaps.
///
/// # Returns
/// * `bool` - True if s1 can be made equal to s2 with the allowable swaps, otherwise False.
fn can_be_equal_with_swaps(s1: &str, s2: &str, pairs: &[(usize, usize)]) -> bool {
    // If the lengths of the strings are not the same, they cannot be equal
    if s1.len() != s2.len() {
        return false;
    }

    let n = s1.len();
    let mut parent: Vec<usize> = (0..n).collect();
    let mut rank: Vec<usize> = vec![0; n];

    // Function to find the root of the component with path compression
    fn find(parent: &mut [usize], i: usize) -> usize {
        if parent[i] != i {
            parent[i] = find(parent, parent[i]);
        }
        parent[i]
    }

    // Function to perform union of two sets with union by rank
    fn union(parent: &mut [usize], rank: &mut [usize], x: usize, y: usize) {
        let root_x = find(parent, x);
        let root_y = find(parent, y);

        if root_x != root_y {
            if rank[root_x] > rank[root_y] {
                parent[root_y] = root_x;
            } else if rank[root_x] < rank[root_y] {
                parent[root_x] = root_y;
            } else {
                parent[root_y] = root_x;
                rank[root_x] += 1;
            }
        }
    }

    // Apply union operation for each pair
    for &(a, b) in pairs {
        union(&mut parent, &mut rank, a, b);
    }

    // Create maps for root to the list of characters that can be swapped in s1 and s2
    let mut root_to_chars_s1: HashMap<usize, Vec<char>> = HashMap::new();
    let mut root_to_chars_s2: HashMap<usize, Vec<char>> = HashMap::new();

    for i in 0..n {
        let root = find(&mut parent, i);
        root_to_chars_s1.entry(root).or_default().push(s1.chars().nth(i).unwrap());
        root_to_chars_s2.entry(root).or_default().push(s2.chars().nth(i).unwrap());
    }

    // Check if the characters in each component can be made equal
    for (root, chars_s1) in root_to_chars_s1 {
        if let Some(chars_s2) = root_to_chars_s2.get(&root) {
            let mut sorted_s1 = chars_s1.clone();
            let mut sorted_s2 = chars_s2.clone();
            sorted_s1.sort_unstable();
            sorted_s2.sort_unstable();
            if sorted_s1 != sorted_s2 {
                return false;
            }
        } else {
            return false;
        }
    }

    true
}

assertions:
#[test]
fn test_can_be_equal_with_swaps() {
    assert!(can_be_equal_with_swaps("abcd", "cbad", &[(0, 1), (1, 2), (2, 3)]));
    assert!(can_be_equal_with_swaps("abcd", "cbad", &[(0, 1), (2, 3)]));
    assert!(can_be_equal_with_swaps("abcd", "abcd", &[]));
    assert!(!can_be_equal_with_swaps("a", "b", &[]));
    assert!(can_be_equal_with_swaps("abc", "bca", &[(0, 1), (1, 2)]));
    assert!(can_be_equal_with_swaps("abc", "bac", &[(0, 1)]));
    assert!(!can_be_equal_with_swaps("abc", "cab", &[(0, 1)]));
}

