use std::cmp::max;

/// Calculates the minimized maximum distance between any robot pairs.
/// The distance between two robots is defined as the absolute difference of their positions.
///
/// # Arguments
/// * `positions` - A slice of integers representing the positions of robots.
///
/// # Returns
/// The minimized maximum distance between any robot pairs as an i32.
fn minimized_max_distance(positions: &mut [i32]) -> i32 {
    // Sort the positions to make pairing adjacent positions easier
    positions.sort();

    // Initialize the minimized maximum distance to 0
    let mut minimized_max_dist = 0;

    // Iterate through the list, pairing adjacent robots (stepping by 2)
    for i in (0..positions.len() - 1).step_by(2) {
        minimized_max_dist = max(minimized_max_dist, positions[i + 1] - positions[i]);
    }

    minimized_max_dist
}
