    assert_eq!(minimized_max_distance(&mut vec![1, 3, 7, 10]), 3);
    assert_eq!(minimized_max_distance(&mut vec![5, 2, 8, 1]), 3);
    assert_eq!(minimized_max_distance(&mut vec![10, 20]), 10);
    assert_eq!(minimized_max_distance(&mut vec![4, 4, 4, 4]), 0);
    assert_eq!(minimized_max_distance(&mut vec![1, 2, 3, 4, 5, 6]), 1);
}

