fn shortest_subarray_to_sort(nums: &[i32]) -> usize {
    let n = nums.len();

    // Edge case: if the array has 1 or 0 elements, it is already sorted
    if n <= 1 {
        return 0;
    }

    // Find the left boundary where the order breaks
    let mut left = 0;
    while left < n - 1 && nums[left] <= nums[left + 1] {
        left += 1;
    }

    // If we reached the end, the array is already sorted
    if left == n - 1 {
        return 0;
    }

    // Find the right boundary where the order breaks
    let mut right = n - 1;
    while right > 0 && nums[right] >= nums[right - 1] {
        right -= 1;
    }

    // Find min and max in the current subarray
    let subarray_min = nums[left..=right].iter().min().unwrap();
    let subarray_max = nums[left..=right].iter().max().unwrap();

    // Expand left boundary if needed
    while left > 0 && nums[left - 1] > *subarray_min {
        left -= 1;
    }

    // Expand right boundary if needed
    while right < n - 1 && nums[right + 1] < *subarray_max {
        right += 1;
    }

    right - left + 1
}

