use std::collections::VecDeque;

// Function to count land clusters in a grid using DFS
// n: number of rows in the grid
// m: number of columns in the grid
// grid: 2D vector representing the grid ('L' for land, 'W' for water)
fn count_land_clusters(n: usize, m: usize, grid: &mut Vec<Vec<char>>) -> i32 {
    // Nested DFS function to explore connected land cells
    fn dfs(x: i32, y: i32, n: i32, m: i32, grid: &mut Vec<Vec<char>>) {
        // Check if current position is out of bounds or not land
        if x < 0 || x >= n || y < 0 || y >= m || grid[x as usize][y as usize] != 'L' {
            return;
        }

        // Mark current cell as visited by changing it to 'W'
        grid[x as usize][y as usize] = 'W';

        // Explore all four adjacent cells
        dfs(x + 1, y, n, m, grid);
        dfs(x - 1, y, n, m, grid);
        dfs(x, y + 1, n, m, grid);
        dfs(x, y - 1, n, m, grid);
    }

    let mut clusters = 0;

    // Iterate through each cell in the grid
    for i in 0..n {
        for j in 0..m {
            // If we find land, perform DFS to mark all connected land
            if grid[i][j] == 'L' {
                dfs(i as i32, j as i32, n as i32, m as i32, grid);
                clusters += 1;
            }
        }
    }

    clusters
}

