// Function to find the longest sequence of elements in a sorted set S where each subsequent element
// increases by a constant step k, starting from an initial value b1.
//
// Parameters:
// - k: The constant step between consecutive elements in the sequence
// - s: A sorted vector of integers representing the set S
// - b1: The starting value of the sequence
//
// Returns:
// A vector containing the longest sequence found
fn longest_jump_sequence(k: i32, s: &[i32], b1: i32) -> Vec<i32> {
    let mut sequence = Vec::new();
    let mut current_value = b1;

    // Binary search the sorted vector to check if current_value exists
    while s.binary_search(&current_value).is_ok() {
        sequence.push(current_value);
        current_value += k;
    }

    sequence
}

