    assert_eq!(longest_jump_sequence(1, &[1, 2, 3, 4, 5], 1), vec![1, 2, 3, 4, 5]);
    assert_eq!(longest_jump_sequence(5, &[10, 15, 20], 10), vec![10, 15, 20]);
    assert_eq!(longest_jump_sequence(2, &[1, 4, 7, 10], 1), vec![1]);
    assert_eq!(longest_jump_sequence(3, &[1, 2, 3], 1), vec![1]);
}

