use std::collections::{HashMap, VecDeque};

/// Finds the order in which tasks can be executed given their dependencies using topological sorting.
/// Returns either the task order as a vector or "Not Possible" if there's a cycle.
fn find_task_order(n: usize, dependencies: &HashMap<usize, Vec<usize>>) -> Result<Vec<usize>, &'static str> {
    // Create adjacency list and compute in-degrees for each task
    let mut adj_list: HashMap<usize, Vec<usize>> = HashMap::new();
    let mut in_degree: HashMap<usize, usize> = (1..=n).map(|task| (task, 0)).collect();

    // Build the adjacency list and count in-degrees
    for (&task, deps) in dependencies {
        for &dep in deps {
            adj_list.entry(dep).or_default().push(task);
            *in_degree.entry(task).or_default() += 1;
        }
    }

    // Initialize queue with tasks that have no dependencies
    let mut queue: VecDeque<usize> = in_degree
        .iter()
        .filter(|&(_, &degree)| degree == 0)
        .map(|(&task, _)| task)
        .collect();

    let mut task_order = Vec::with_capacity(n);

    // Process tasks in topological order
    while let Some(current_task) = queue.pop_front() {
        task_order.push(current_task);

        if let Some(neighbors) = adj_list.get(&current_task) {
            for &neighbor in neighbors {
                if let Some(degree) = in_degree.get_mut(&neighbor) {
                    *degree -= 1;
                    if *degree == 0 {
                        queue.push_back(neighbor);
                    }
                }
            }
        }
    }

    // Check if all tasks were processed (no cycles)
    if task_order.len() == n {
        Ok(task_order)
    } else {
        Err("Not Possible")
    }
}

/// Parses input into the number of tasks and their dependencies
fn parse_input(input_lines: &[&str]) -> (usize, HashMap<usize, Vec<usize>>) {
    let n: usize = input_lines[0].parse().unwrap();
    let mut dependencies = HashMap::new();

    for line in &input_lines[1..=n] {
        let parts: Vec<usize> = line.split_whitespace().map(|s| s.parse().unwrap()).collect();
        let task = parts[0];
        let deps = if parts[1] == 0 { // Using 0 instead of -1 for Rust's usize type
            vec![]
        } else {
            parts[1..].to_vec()
        };
        dependencies.insert(task, deps);
    }

    (n, dependencies)
}

