    ];
    let (n1, deps1) = parse_input(&input1);
    assert_eq!(find_task_order(n1, &deps1), Ok(vec![1, 2, 3]));

    // Test case 2: More complex dependencies
    let input2 = vec![
        "5",
        "1 0",
        "2 1 0",
        "3 1 0",
        "4 2 3 0",
        "5 4 0",
