    let input3 = vec![
        "3",
        "1 2 0",
        "2 3 0",
        "3 1 0",
    ];
    let (n3, deps3) = parse_input(&input3);
    assert_eq!(find_task_order(n3, &deps3), Err("Not Possible"));

