use std::collections::{HashMap, VecDeque};

/// A stack-like data structure that allows pushing elements and popping
/// the most frequent element. If there are ties, the most recently pushed
/// element is returned.
struct FreqStack {
    freq: HashMap<i32, i32>,          // Tracks frequency of each value
    group: HashMap<i32, VecDeque<i32>>, // Groups values by their frequency
    max_freq: i32,                    // Tracks the current maximum frequency
}

impl FreqStack {
    /// Creates a new, empty FreqStack
    fn new() -> Self {
        FreqStack {
            freq: HashMap::new(),
            group: HashMap::new(),
            max_freq: 0,
        }
    }

    /// Pushes a value onto the stack
    fn push(&mut self, val: i32) {
        // Update the frequency count for this value
        let count = self.freq.entry(val).or_insert(0);
        *count += 1;
        let current_freq = *count;

        // Update max_freq if needed
        if current_freq > self.max_freq {
            self.max_freq = current_freq;
        }

        // Add the value to its frequency group
        self.group.entry(current_freq).or_default().push_back(val);
    }

    /// Pops and returns the most frequent element.
    /// If there are ties, returns the most recently pushed element.
    fn pop(&mut self) -> i32 {
        // Get the group with max frequency
        if let Some(group) = self.group.get_mut(&self.max_freq) {
            if let Some(val) = group.pop_back() {
                // Update the frequency count
                if let Some(count) = self.freq.get_mut(&val) {
                    *count -= 1;
                }

                // If this group is now empty, decrease max_freq
                if group.is_empty() {
                    self.max_freq -= 1;
                }

                return val;
            }
        }
        panic!("Cannot pop from empty FreqStack");
    }
}

