use std::collections::{VecDeque, HashSet};

/// Determines if there's a path from 'S' to 'X' in the given grid.
/// Returns "YES" if a path exists, "NO" otherwise.
///
/// # Arguments
/// * `n` - Number of rows in the grid
/// * `m` - Number of columns in the grid
/// * `grid` - 2D vector representing the grid with characters
fn can_reach_destination(n: usize, m: usize, grid: &Vec<Vec<char>>) -> &'static str {
    // Find the starting point 'S' and destination 'X'
    let mut start = None;
    let mut end = None;

    for i in 0..n {
        for j in 0..m {
            if grid[i][j] == 'S' {
                start = Some((i, j));
            }
            if grid[i][j] == 'X' {
                end = Some((i, j));
            }

            if start.is_some() && end.is_some() {
                break;
            }
        }
        if start.is_some() && end.is_some() {
            break;
        }
    }

    let start = match start {
        Some(pos) => pos,
        None => return "NO",
    };

    let end = match end {
        Some(pos) => pos,
        None => return "NO",
    };

    // Directions for moving in the grid (right, down, left, up)
    let directions = [(0, 1), (1, 0), (0, -1), (-1, 0)];

    // BFS algorithm to find path from 'S' to 'X'
    let mut queue = VecDeque::new();
    queue.push_back(start);

    let mut visited = HashSet::new();
    visited.insert(start);

    while let Some((x, y)) = queue.pop_front() {
        if (x, y) == end {
            return "YES";
        }

        for &(dx, dy) in &directions {
            let nx = x as i32 + dx;
            let ny = y as i32 + dy;

            if nx >= 0 && ny >= 0 {
                let nx = nx as usize;
                let ny = ny as usize;

                if nx < n && ny < m && grid[nx][ny] != '#' && !visited.contains(&(nx, ny)) {
                    queue.push_back((nx, ny));
                    visited.insert((nx, ny));
                }
            }
        }
    }

    "NO"
}

