use std::collections::{HashMap, VecDeque, HashSet};

/// Determines the relationship between two employees in a hierarchy.
///
/// # Arguments
/// * `report_list` - A slice of tuples where the first element is the manager and the second is the direct report.
/// * `target_pair` - A tuple where we determine the relationship of the first employee to the second.
///
/// # Returns
/// The relationship as a string slice: "Manager", "Direct Report", "Indirect Report", "Colleague", or "None".
fn determine_relationship(report_list: &[(&str, &str)], target_pair: (&str, &str)) -> &'static str {
    // Build a manager-to-reports mapping
    let mut manager_dict: HashMap<&str, Vec<&str>> = HashMap::new();
    for &(manager, report) in report_list {
        manager_dict.entry(manager).or_default().push(report);
    }

    /// Breadth-first search to find the depth of relationship between employees
    fn bfs(manager_dict: &HashMap<&str, Vec<&str>>, start: &str, target: &str) -> Option<usize> {
        let mut queue = VecDeque::new();
        let mut visited = HashSet::new();
        queue.push_back((start, 0));

        while let Some((current, depth)) = queue.pop_front() {
            if current == target {
                return Some(depth);
            }
            visited.insert(current);
            if let Some(reports) = manager_dict.get(current) {
                for &next_employee in reports {
                    if !visited.contains(next_employee) {
                        queue.push_back((next_employee, depth + 1));
                    }
                }
            }
        }
        None
    }

    let (employee1, employee2) = target_pair;

    // Check for direct report relationship
    if manager_dict.get(employee2)
        .map_or(false, |reports| reports.contains(&employee1)) {
        return "Direct Report";
    }

    // Check for indirect report relationship
    if let Some(_) = bfs(&manager_dict, employee2, employee1) {
        return "Indirect Report";
    }

    // Check for colleague relationship
    for (manager, reports) in &manager_dict {
        if reports.contains(&employee1) && reports.contains(&employee2) {
            return "Colleague";
        }
    }

    // Check for manager relationship
    if bfs(&manager_dict, employee1, employee2) == Some(1) {
        return "Manager";
    }

    // No relationship found
    "None"
}

