use std::cmp::{max, min};

/// Determines if the array is k-diverse.
/// A k-diverse array is one where the difference between the maximum
/// and minimum values in any contiguous subarray of length k is  k.
fn is_k_diverse(arr: &[i32], k: usize) -> bool {
    let n = arr.len();
    if k == 1 {
        return true;
    }

    for i in 0..(n - k + 1) {
        let subarray = &arr[i..i + k];
        let subarray_max = subarray.iter().max().unwrap();
        let subarray_min = subarray.iter().min().unwrap();
        if subarray_max - subarray_min > k as i32 {
            return false;
        }
    }

    true
}

