    assert_eq!(is_k_diverse(&[1, 2, 3, 4], 2), true);
    assert_eq!(is_k_diverse(&[1, 2, 3, 4], 3), true);
    assert_eq!(is_k_diverse(&[1, 2, 3, 4], 4), true);
    assert_eq!(is_k_diverse(&[1, 5, 3, 4], 2), false);
    assert_eq!(is_k_diverse(&[1, 5, 3, 4], 3), true);
}

