use std::collections::HashMap;

// Function to find the minimum number of edges to remove to split the graph into two connected components
// using a Kruskal's algorithm approach with Union-Find (Disjoint Set Union)
fn min_edges_to_remove(n: usize, m: usize, edges: &mut Vec<(usize, usize, i32)>) -> usize {
    // Union-Find data structure implementation
    struct UnionFind {
        parent: Vec<usize>,
        rank: Vec<usize>,
    }

    impl UnionFind {
        fn new(size: usize) -> Self {
            UnionFind {
                parent: (0..size).collect(),
                rank: vec![0; size],
            }
        }

        fn find(&mut self, u: usize) -> usize {
            if self.parent[u] != u {
                self.parent[u] = self.find(self.parent[u]);
            }
            self.parent[u]
        }

        fn union(&mut self, u: usize, v: usize) {
            let root_u = self.find(u);
            let root_v = self.find(v);

            if root_u != root_v {
                if self.rank[root_u] > self.rank[root_v] {
                    self.parent[root_v] = root_u;
                } else if self.rank[root_u] < self.rank[root_v] {
                    self.parent[root_u] = root_v;
                } else {
                    self.parent[root_v] = root_u;
                    self.rank[root_u] += 1;
                }
            }
        }
    }

    // Sort edges by weight in ascending order
    edges.sort_by_key(|&(_, _, w)| w);

    let mut uf = UnionFind::new(n + 1); // 1-based indexing
    let mut mst_edges = Vec::new();

    // Kruskal's algorithm to find MST
    for &(u, v, w) in edges.iter() {
        if uf.find(u) != uf.find(v) {
            uf.union(u, v);
            mst_edges.push((u, v, w));
        }
    }

    // The answer is 1 if we have an MST, otherwise 0
    if mst_edges.is_empty() { 0 } else { 1 }
}

