                } else if self.rank[root_u] < self.rank[root_v] {
                    self.parent[root_u] = root_v;
                } else {
                    self.parent[root_v] = root_u;
                    self.rank[root_u] += 1;
                }
            }
        }
    }

    // Sort edges by weight in ascending order
    edges.sort_by_key(|&(_, _, w)| w);

    let mut uf = UnionFind::new(n + 1); // 1-based indexing
    let mut mst_edges = Vec::new();

    // Kruskal's algorithm to find MST
    for &(u, v, w) in edges.iter() {
        if uf.find(u) != uf.find(v) {
            uf.union(u, v);
            mst_edges.push((u, v, w));
        }
    }

    // The answer is 1 if we have an MST, otherwise 0
    if mst_edges.is_empty() { 0 } else { 1 }
}
#[test]
fn test_min_edges_to_remove() {
    let test_cases = vec![
        (
            4,  // n
            5,  // m
            vec![(1, 2, 1), (2, 3, 2), (3, 4, 3), (4, 1, 4), (2, 4, 5)],  // edges
            1   // expected result
        ),
        (
            3,  // n
            3,  // m
            vec![(1, 2, 1), (2, 3, 2), (1, 3, 3)],  // edges
            1   // expected result
        ),
        (
            2,  // n
            1,  // m
            vec![(1, 2, 5)],  // edges
            0   // expected result (already disconnected)
        ),
        (
            5,  // n
            7,  // m
            vec![(1, 2, 1), (2, 3, 2), (3, 4, 3), (4, 5, 4), (1, 5, 5), (2, 5, 6), (3, 5, 7)],  // edges
            1   // expected result
        ),
    ];

    for (i, (n, m, mut edges, expected)) in test_cases.into_iter().enumerate() {
        let result = min_edges_to_remove(n, m, &mut edges);
        assert_eq!(result, expected, "Test case {} failed", i + 1);
    }
}

